import React, { useState } from 'react'
import { SupabaseImageStorage } from '../../lib/supabase-storage'

const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

export function ImageWithFallback(props: React.ImgHTMLAttributes<HTMLImageElement>) {
  const [didError, setDidError] = useState(false)

  const handleError = () => {
    setDidError(true)
  }

  const { src, alt, style, className, ...rest } = props

  // Convert Supabase storage path to full URL if needed
  const imageUrl = src && !src.startsWith('http') && !src.startsWith('data:') 
    ? SupabaseImageStorage.getImageUrl(src)
    : src

  return didError ? (
    <div
      className={`inline-block bg-gray-800 text-center align-middle ${className ?? ''}`}
      style={style}
    >
      <div className="flex items-center justify-center w-full h-full">
        <img src={ERROR_IMG_SRC} alt="Error loading image" {...rest} data-original-url={src} />
      </div>
    </div>
  ) : (
    <img src={imageUrl} alt={alt} className={className} style={style} {...rest} onError={handleError} />
  )
}
